import "../styles/globals.css";
import { motion, AnimatePresence } from "framer-motion";

// In pages/_app.js
function App({ Component, pageProps, router }) {
  return (
    <AnimatePresence>
      <motion.div
        key={router.route}
        initial="pageInitial"
        animate="pageAnimate"
        variants={{
          pageInitial: {
            opacity: 0,
          },
          pageAnimate: {
            opacity: 1,
            transition: {
              duration: 1.25,
            },
          },
        }}
      >
        <Component {...pageProps} />;
      </motion.div>
    </AnimatePresence>
  );
}

export default App;
