import "../styles/globals.css";
import { motion } from "framer-motion";

// In pages/_app.js
function App({ Component, pageProps }) {
  return (
    <motion.div
      initial="pageInitial"
      animate="pageAnimate"
      variants={{
        pageInitial: {
          opacity: 0,
        },
        pageAnimate: {
          opacity: 1,
          transition: {
            duration: 2,
          },
        },
      }}
    >
      <Component {...pageProps} />;
    </motion.div>
  );
}

export default App;
